export default class ItemsAPI {
  /**
   *
   * @param {Array} items
   */
  constructor(items) {
    this.items = items;
  }

  setItems(items) {
    this.items = items;
  }

  getItems() {
    return this.items;
  }

  addItem(item) {
    this.items.push(item);
  }
}
