import React, { useState, useEffect, useCallback } from "react";
import ItemList from "./ItemsList/ItemList";
import ItemListSelf from "./ItemsList/ItemListSelf";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import Backdrop from "@mui/material/Backdrop";
import CircularProgress from "@mui/material/CircularProgress";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import API from "../utils/API";

const timeRefresh = 2000;

const item_shem = {
  name: "",
  description: "",
  price: 0,
};

let time = 0;
setInterval(() => {
  time++;
}, 1000);

export default function Market() {
  let navigate = useNavigate();
  const [, updateState] = useState();
  const forceUpdate = useCallback(() => updateState({}), []);
  const [value, setValue] = useState();
  const [open, setOpen] = useState(false);
  const [myId, set_myId] = useState({});
  const [money, set_money] = useState(0);
  const [new_item, set_new_item] = useState(item_shem);
  const [items, set_items] = useState([]);

  useEffect(() => {
    axios
      .get(`${API.url()}/users/find/${API.getUserId()}`)
      .then((res) => {
        set_myId(res.data);
      })
      .catch((e) => console.log(e));
  }, []);

  const Information = () => {
    return (
      <Box sx={{ textAlign: "center" }}>
        <h1>Bienvenue {myId.pseudo}</h1>
        <h2>Argent disponible : {myId.money} â‚¬</h2>
      </Box>
    );
  };

  const HandleAddItem = () => {
    console.log(new_item);
    API.createItem(new_item)
      .then((_) => {
        set_items((oldItem) => [...oldItem, new_item]);
        set_new_item(item_shem);
      })
      .catch((_) => console.log("erreur creation d'item"));
  };
  const Logout = () => {
    return (
      <Box>
        <Button
          color="secondary"
          onClick={() => {
            API.logout();
            navigate("/login");
          }}
        >
          DECONNEXION
        </Button>
      </Box>
    );
  };

  const AddMoney = () => {
    return (
      <Box sx={{ textAlign: "center" }}>
        <Box>
          <h2>Ajout d'argent</h2>
          <TextField
            type="number"
            onChange={(e) => set_money(e.target.value)}
            value={money}
            label="Ajout d'argent"
          ></TextField>
        </Box>
        <Box>
          <Button
            onClick={() => {
              let current_money = parseInt(myId.money);
              console.log(current_money);
              console.log(money);
              current_money += parseInt(money);
              if (current_money < 0) current_money = 0;
              console.log(current_money);
              API.setMoney(current_money);
              set_myId({
                ...myId,
                money: current_money,
              });
              // setOpen(true);
              setTimeout(() => {
                // window.location.reload(false);
                forceUpdate();
                // setOpen(false);
                set_money(0);
              }, timeRefresh);
              forceUpdate();
            }}
          >
            ENVOI
          </Button>
        </Box>
      </Box>
    );
  };

  const AddItem = () => {
    return (
      <Box sx={{ textAlign: "center" }}>
        <h2> Ajout d'un nouvel item</h2>
        <div>
          <TextField
            type="text"
            onChange={(e) =>
              set_new_item({
                ...new_item,
                name: e.target.value,
              })
            }
            value={new_item.name}
            label="nom"
            multiline
          ></TextField>
          <TextField
            type="text"
            onChange={(e) =>
              set_new_item({
                ...new_item,
                description: e.target.value,
              })
            }
            value={new_item.description}
            label="description"
            multiline
          ></TextField>
          <TextField
            sx={{ width: "15ch" }}
            id="standard-number"
            type="number"
            onChange={(e) =>
              set_new_item({
                ...new_item,
                price: e.target.value,
              })
            }
            value={new_item.price}
            InputLabelProps={{
              shrink: true,
            }}
            label="prix"
          ></TextField>
        </div>
        <Box>
          <Button onClick={() => HandleAddItem()}>ENVOI</Button>
        </Box>
      </Box>
    );
  };
  return (
    <div>
      {Information()}
      <Grid container>
        <ItemList money={myId.money} items={items} id={myId._id} />{" "}
        <ItemListSelf items={items} id={myId._id} />
      </Grid>
      {AddItem()}
      {AddMoney()} {Logout()}
    </div>
  );
}
