import React, { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import Backdrop from "@mui/material/Backdrop";
import CircularProgress from "@mui/material/CircularProgress";
import Feedback from "../Feedback";
import API from "../../utils/API";
import { Divider } from "@mui/material";

export default function ItemListSelf() {
  const [items, set_items] = useState([]);
  const [open, set_open] = useState(false);
  const [show_change, set_show_change] = useState(false);
  const [item_change, set_item_change] = useState({});

  useEffect(() => {
    API.getItemUser().then((e) => set_items(e));
  }, []);

  const HandleChange = (item) => {
    console.log(item);
    set_item_change(item);
    set_show_change(true);
  };

  const HandleDelete = (item) => {
    set_open(true);
    API.deleteItem(item._id);
    setTimeout(() => {
      window.location.reload(false);
      set_open(false);
    }, 2000);
  };

  const Item_Change = () => {
    return (
      <Backdrop
        sx={{ color: "#fff", zIndex: (theme) => theme.zIndex.drawer + 1 }}
        open={show_change}
        onClick={() => set_show_change(false)}
      >
        <CircularProgress color="inherit" />
      </Backdrop>
    );
  };

  const List_items = () => {
    return (
      <div>
        <h2>MES OBJETS</h2>
        <List>
          {items.map((item, n) => {
            let price = item.price + " â‚¬";
            return (
              <>
                <ListItem key={n}>
                  <ListItemText primary={item.name} secondary={price} />
                </ListItem>
                <ListItem key={n*100}>
                  <Button onClick={() => HandleChange(item)}>Modifier</Button>
                  <Button color="secondary" onClick={() => HandleDelete(item)}>
                    Supprimer
                  </Button>
                </ListItem>
                <Divider />
              </>
            );
          })}
        </List>
      </div>
    );
  };
  return (
    <div>
      <Backdrop
        sx={{ color: "#fff", zIndex: (theme) => theme.zIndex.drawer + 1 }}
        open={open}
      >
        <CircularProgress color="inherit" />
      </Backdrop>
      {Item_Change()}
      {List_items()}
    </div>
  );
}
