import React, { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";
import List from "@mui/material/List";
import ListItemText from "@mui/material/ListItemText";
import API from "../../utils/API";
import Divider from "@mui/material/Divider";
import Backdrop from "@mui/material/Backdrop";
import CircularProgress from "@mui/material/CircularProgress";
import ListItemButton from "@mui/material/ListItemButton";

export default function ItemList(props) {
  const [items, set_items] = useState([]);
  const [shop, set_shop] = useState({});
  const [open, setOpen] = useState(false);
  const [loading, set_loading] = useState(false);

  useEffect(() => {
    API.getItemOther().then((e) => set_items(e));
    console.log(items);
  }, []);

  const handlePurchase = (item) => {
    set_shop(item);
    setOpen(true);
  };

  const handleTake = () => {
    let current_money = props.money;
    console.log(`${current_money}`);

    if (current_money >= shop.price) {
      current_money -= shop.price;
      set_loading(true);
      API.setMoney(current_money);
      API.buyItem(shop._id);
      window.setTimeout(() => {
        window.location.reload(false);
      }, 2000);
    } else {
      console.log("argent insuffisant !");
    }
    set_shop({});
  };

  const List_items = () => {
    return (
      <Box sx={{ width: 1 / 2, textAlign: "center" }}>
        <h2>A VENDRE</h2>
        <List key={"liItem"} sx={{ textAlign: "center" }}>
          {items.map((item, n) => {
            let description = item.description;
            let name = item.name + " | " + item.price + " â‚¬";
            return (
              <>
                <ListItemButton key={name} onClick={() => handlePurchase(item)}>
                  <ListItemText
                    sx={{ textAlign: "center" }}
                    primary={name}
                    secondary={description}
                  />
                </ListItemButton>
                <Divider />
              </>
            );
          })}
        </List>
      </Box>
    );
  };
  return (
    <>
      <Backdrop sx={{ color: "#fff" }} open={open}>
        {!loading ? (
          <>
            <Box>
              <h1>
                Acheter un/une {shop.name} pour {shop.price} â‚¬ ?
              </h1>
            </Box>
            <Box>
              <Button size="large" onClick={() => handleTake()}>
                OUI
              </Button>
              <Button
                size="large"
                color="secondary"
                onClick={() => setOpen(false)}
              >
                NON
              </Button>
            </Box>
          </>
        ) : (
          <>
            <CircularProgress color="inherit" />
          </>
        )}
      </Backdrop>
      {List_items()}
    </>
  );
}
