import React from "react";
import ItemList from "./ItemsList/ItemList";
import ItemListSelf from "./ItemsList/ItemListSelf";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import axios from "axios";
import API from "../utils/API";
import { Link } from "react-router-dom";
const timeRefresh = 2000;

const item_shem = {
  name: "",
  description: "",
  price: 0,
};

class Market extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      myId: {},
      money: 0,
      new_item: {},
      items: [],
    };
  }

  componentDidMount() {
    axios
      .get(`${API.url()}/users/find/${API.getUserId()}`)
      .then((res) => {
        this.setState({ myId: res.data });
      })
      .catch((e) => console.log(e));

    axios
      .get(`${API.url()}/items/all`)
      .then((res) => {
        console.log(res.data);
        const items = res.data;
        this.setState({ items });
      })
      .catch((e) => {
        console.log(e);
        return [];
      });
    // API.getItemAll().then((res) => this.setState({ items: res }));
    console.log(this.state.items);
  }

  Information() {
    return (
      <Box sx={{ textAlign: "center" }}>
        <h1>Bienvenue {this.state.myId.pseudo}</h1>
        <h2>Argent disponible : {this.state.myId.money} â‚¬</h2>
      </Box>
    );
  }

  HandleAddItem() {
    console.log(this.state.new_item);
    API.createItem(this.state.new_item)
      .then((_) => {
        this.setState({ items: [...this.state.items, this.state.new_item] });
        this.setState({ new_item: item_shem });
      })
      .catch((_) => console.log("erreur creation d'item"));
  }

  Logout() {
    return (
      <Box>
        <Button
          color="secondary"
          onClick={() => {
            API.logout();
          }}
        >
          <Link to="/login">DECONNEXION</Link>
        </Button>
      </Box>
    );
  }

  AddMoney() {
    return (
      <Box sx={{ textAlign: "center" }}>
        <Box>
          <h2>Ajout d'argent</h2>
          <TextField
            type="number"
            onChange={(e) => this.setState({ money: e.target.value })}
            value={this.state.money}
            label="Ajout d'argent"
          ></TextField>
        </Box>
        <Box>
          <Button
            onClick={() => {
              let current_money = parseInt(this.state.myId.money);
              console.log(current_money);
              console.log(this.state.money);
              current_money += parseInt(this.state.money);
              if (current_money < 0) current_money = 0;
              console.log(current_money);
              API.setMoney(current_money);
              this.setState({
                myId: {
                  money: current_money,
                },
              });
              setTimeout(() => {
                this.set_money(0);
              }, timeRefresh);
            }}
          >
            ENVOI
          </Button>
        </Box>
      </Box>
    );
  }

  AddItem() {
    return (
      <Box sx={{ textAlign: "center" }}>
        <h2> Ajout d'un nouvel item</h2>
        <div>
          <TextField
            type="text"
            onChange={(e) =>
              this.setState({
                new_item: {
                  name: e.target.value,
                },
              })
            }
            value={this.state.new_item.name}
            label="nom"
            multiline
          ></TextField>
          <TextField
            type="text"
            onChange={(e) =>
              this.setState({
                new_item: {
                  description: e.target.value,
                },
              })
            }
            value={this.state.new_item.description}
            label="description"
            multiline
          ></TextField>
          <TextField
            sx={{ width: "15ch" }}
            id="standard-number"
            type="number"
            onChange={(e) =>
              this.setState({
                new_item: {
                  price: e.target.value,
                },
              })
            }
            value={this.state.new_item.price}
            InputLabelProps={{
              shrink: true,
            }}
            label="prix"
          ></TextField>
        </div>
        <Box>
          <Button onClick={() => this.HandleAddItem()}>ENVOI</Button>
        </Box>
      </Box>
    );
  }
  render() {
    return (
      <div>
        {this.Information()}
        <Grid container>
          <ItemList
            money={this.state.myId.money}
            items={this.state.items}
            id={this.state.myId._id}
          />{" "}
          <ItemListSelf items={this.state.items} id={this.state.myId._id} />
        </Grid>
        {this.AddItem()}
        {this.AddMoney()} {this.Logout()}
      </div>
    );
  }
}

export default Market;
