import React, { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import Backdrop from "@mui/material/Backdrop";
import CircularProgress from "@mui/material/CircularProgress";
import Feedback from "../Feedback";
import API from "../../utils/API";
import { Divider } from "@mui/material";

export default function ItemListSelf() {
  const [items, set_items] = useState([]);
  const [open, set_open] = useState(false);
  const [show_status, set_show_status] = useState(false);
  const [status, set_status] = useState({});

  useEffect(() => {
    API.getItemUser().then((e) => set_items(e));
  }, []);

  const HandleChange = (item) => {
    console.log(item);
  };
  const HandleDelete = (item) => {
    let item_info = item;
    set_open(true);
    API.deleteItem(item._id)
      .then((e) =>
        set_status({
          status: { status: 200, statusTest: `${item_info.name} Supprimé` },
        })
      )
      .catch((e) => set_status(e));
    set_show_status(true);
    setTimeout(() => {
      window.location.reload(false);
      set_open(false);
    }, 3000);
  };

  const List_items = () => {
    return (
      <div>
        <h2>MES OBJETS</h2>
        <List>
          {items.map((item, n) => {
            let price = item.price + " €";
            return (
              <>
                <ListItem key={n}>
                  <ListItemText primary={item.name} secondary={price} />
                </ListItem>
                <ListItem>
                  <Button onClick={() => HandleChange(item)}>Modifier</Button>
                  <Button color="secondary" onClick={() => HandleDelete(item)}>
                    Supprimer
                  </Button>
                </ListItem>
                <Divider />
              </>
            );
          })}
        </List>
      </div>
    );
  };
  return (
    <div>
      <Backdrop
        sx={{ color: "#fff", zIndex: (theme) => theme.zIndex.drawer + 1 }}
        open={open}
      >
        <CircularProgress color="inherit" />
      </Backdrop>
      {List_items()}
      {Feedback({ status, close: !show_status })}
    </div>
  );
}
