import React, { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import Box from "@mui/material/Box";
import List from "@mui/material/List";
import ListItemText from "@mui/material/ListItemText";
import API from "../../utils/API";
import Divider from "@mui/material/Divider";
import Backdrop from "@mui/material/Backdrop";
import ListItemButton from "@mui/material/ListItemButton";
import Feedback from "../Feedback";

const timeRefresh = 1500;

export default function ItemList(props) {
  const [shop, set_shop] = useState({});
  const [items, set_items] = useState(props.items);
  const [open, setOpen] = useState(false);
  const [not_money, set_not_money] = useState(false);

  useEffect(() => {
    set_items(props.items);
  },[props.items]);

  const handlePurchase = (item) => {
    set_shop(item);
    console.log(item);
    setOpen(true);
  };

  const handleTake = () => {
    let current_money = props.money;

    if (current_money >= shop.price) {
      current_money -= shop.price;
      API.setMoney(current_money);
      API.buyItem(shop);
    } else {
      set_not_money(true);
      window.setTimeout(() => set_not_money(false), timeRefresh + 2000);
    }
    setOpen(false);
    set_shop({});
  };

  const List_items = () => {
    const otherItems = items.filter((item) => item.id_owner !== props.id);
    return (
      <Box sx={{ width: 1 / 2, textAlign: "center" }}>
        <h2>A VENDRE</h2>
        <List key={"liItem"} sx={{ textAlign: "center" }}>
          {otherItems.map((item, n) => {
            let description = item.description;
            let name = item.name + " | " + item.price + " â‚¬";
            return (
              <div key={name + "55"}>
                <ListItemButton key={name} onClick={() => handlePurchase(item)}>
                  <ListItemText
                    sx={{ textAlign: "center" }}
                    primary={name}
                    secondary={description}
                  />
                </ListItemButton>
                <Divider />
              </div>
            );
          })}
        </List>
      </Box>
    );
  };

  return (
    <>
      <Backdrop sx={{ color: "#fff" }} open={open}>
        <Box>
          <h1>
            Acheter un/une {shop.name} pour {shop.price} â‚¬ ?
          </h1>
        </Box>
        <Box>
          <Button size="large" onClick={() => handleTake()}>
            OUI
          </Button>
          <Button size="large" color="secondary" onClick={() => setOpen(false)}>
            NON
          </Button>
        </Box>
      </Backdrop>
      {List_items()}
      {Feedback({ ok: false, text: "Argent insuffisant !", open: not_money })}
    </>
  );
}
