import React, { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import Backdrop from "@mui/material/Backdrop";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import API from "../../utils/API";
import { Divider } from "@mui/material";

const item_shem = {
  name: "",
  description: "",
  price: 0,
};

export default function ItemListSelf(props) {
  const [items, set_items] = useState([]);
  const [show_change, set_show_change] = useState(false);
  const [item_change, set_item_change] = useState(item_shem);

  useEffect(() => {
    set_items(props.items);
    List_items();
  }, [[props.items],[List_items]]);

  const HandleChange = (item) => {
    console.log(item);
    set_item_change(item);
    set_show_change(true);
  };

  const SendChange = () => {
    API.changeItem(item_change._id, item_change);
    let itemsChange = items.map((item) => {
      if (item._id === item_change._id) item = item_change;
      return item;
    });
    set_items(itemsChange);
    set_item_change(item_shem);
    set_show_change(false);
  };

  const HandleSell = (item) => {
    item.sell = !item.sell;
    console.log(item.sell);
    API.setSellItem(item._id, item.sell);
    set_items(items);
    // items.map((it) => {
    //   if (it._id === item._id) item.sell = change;
    // });
  };

  const HandleDelete = (item) => {
    API.deleteItem(item._id);
    let newItem = items.filter((i) => i._id !== item._id);
    set_items(newItem);
  };

  const Item_Change = () => {
    return (
      <Backdrop
        sx={{ color: "#fff", zIndex: (theme) => theme.zIndex.drawer + 1 }}
        open={show_change}
      >
        <Box
          component="form"
          sx={{
            "& .MuiTextField-root": { m: 1, width: "25ch" },
          }}
          noValidate
          autoComplete="off"
        >
          <h2> Changement de l'objet {item_change.name}</h2>
          <div>
            <TextField
              multiline
              variant="filled"
              defaultValue={item_change.name}
              id="outlined-required"
              label="nom"
              onChange={(e) =>
                set_item_change({
                  ...item_change,
                  name: e.target.value,
                })
              }
            ></TextField>
            <TextField
              multiline
              variant="filled"
              label="description"
              onChange={(e) =>
                set_item_change({
                  ...item_change,
                  description: e.target.value,
                })
              }
              defaultValue={item_change.description}
            ></TextField>
            <TextField
              multiline
              variant="filled"
              sx={{ width: "12ch" }}
              label="prix"
              id="standard-number"
              type="number"
              onChange={(e) =>
                set_item_change({
                  ...item_change,
                  price: e.target.value,
                })
              }
              defaultValue={item_change.price}
            ></TextField>
          </div>
          <Box>
            <Button onClick={() => SendChange()}>ENVOI</Button>
            <Button color="secondary" onClick={() => set_show_change(false)}>
              Annuler
            </Button>
          </Box>
        </Box>
      </Backdrop>
    );
  };

  const List_items = () => {
    return (
      <Box sx={{ width: 1, paddingLeft: 1 / 2, textAlign: "center" }}>
        <h2>MES OBJETS</h2>
        <List key={9999}>
          {items
            .filter((item) => item.id_owner === props.id)
            .map((item, n) => {
              let price = item.price + " â‚¬";
              return (
                <div key={item.name + "55"}>
                  <ListItem key={item.name}>
                    <ListItemText
                      sx={{ textAlign: "center" }}
                      key={item.name + " ok"}
                      primary={item.name}
                      secondary={price}
                    />
                  </ListItem>
                  <ListItem key={n}>
                    <Button
                      key={item.name + "55"}
                      onClick={() => HandleChange(item)}
                    >
                      Modifier
                    </Button>
                    <Button
                      key={item.name + "85"}
                      color="secondary"
                      onClick={() => HandleDelete(item)}
                    >
                      Supprimer
                    </Button>
                    <Button
                      key={item.name + "7"}
                      color="info"
                      onClick={() => HandleSell(item)}
                    >
                      {item.sell ? "Retirer de la vente" : "Mettre en vente"}
                    </Button>
                  </ListItem>
                  <Divider />
                </div>
              );
            })}
        </List>
      </Box>
    );
  };
  return (
    <div>
      {Item_Change()}
      {List_items()}
    </div>
  );
}
