import React, { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemText from "@mui/material/ListItemText";
import Backdrop from "@mui/material/Backdrop";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import CircularProgress from "@mui/material/CircularProgress";
import Feedback from "../Feedback";
import API from "../../utils/API";
import { Divider } from "@mui/material";

export default function ItemListSelf() {
  const [items, set_items] = useState([]);
  const [open, set_open] = useState(false);
  const [show_change, set_show_change] = useState(false);
  const [item_change, set_item_change] = useState({});

  useEffect(() => {
    API.getItemUser().then((e) => set_items(e));
  }, []);

  const HandleChange = (item) => {
    console.log(item);
    set_item_change(item);
    set_show_change(true);
  };

  const SendChange = () => {
    API.changeItem(item_change._id, item_change);
    set_show_change(false);
    set_open(true)
    setTimeout(() => {
      window.location.reload(false);
      set_open(false);
    }, 2000);
  };

  const HandleDelete = (item) => {
    set_open(true);
    API.deleteItem(item._id);
    setTimeout(() => {
      window.location.reload(false);
      set_open(false);
    }, 2000);
  };

  const Item_Change = () => {
    return (
      <Backdrop
        sx={{ color: "#fff", zIndex: (theme) => theme.zIndex.drawer + 1 }}
        open={show_change}
      >
        <Box
          component="form"
          sx={{
            "& .MuiTextField-root": { m: 1, width: "25ch" },
          }}
          noValidate
          autoComplete="off"
        >
          <h2> Changement de l'objet {item_change.name}</h2>
          <div>
            <TextField
              multiline
              variant="filled"
              defaultValue={item_change.name}
              id="outlined-required"
              label="nom"
              onChange={(e) =>
                set_item_change({
                  ...item_change,
                  name: e.target.value,
                })
              }
            ></TextField>
            <TextField
              multiline
              variant="filled"
              label="description"
              onChange={(e) =>
                set_item_change({
                  ...item_change,
                  description: e.target.value,
                })
              }
              defaultValue={item_change.description}
            ></TextField>
            <TextField
              multiline
              variant="filled"
              sx={{ width: "12ch" }}
              label="prix"
              id="standard-number"
              type="number"
              onChange={(e) =>
                set_item_change({
                  ...item_change,
                  price: e.target.value,
                })
              }
              defaultValue={item_change.price}
            ></TextField>
          </div>
          <Box>
            <Button onClick={() => SendChange()}>ENVOI</Button>
            <Button color="secondary" onClick={() => set_show_change(false)}>
              Annuler
            </Button>
          </Box>
        </Box>
      </Backdrop>
    );
  };

  const List_items = () => {
    return (
      <div>
        <h2>MES OBJETS</h2>
        <List key={9999}>
          {items.map((item, n) => {
            let price = item.price + " €";
            return (
              <>
                <ListItem key={item.name}>
                  <ListItemText
                    key={item.name + " ok"}
                    primary={item.name}
                    secondary={price}
                  />
                </ListItem>
                <ListItem key={n}>
                  <Button onClick={() => HandleChange(item)}>Modifier</Button>
                  <Button color="secondary" onClick={() => HandleDelete(item)}>
                    Supprimer
                  </Button>
                </ListItem>
                <Divider />
              </>
            );
          })}
        </List>
      </div>
    );
  };
  return (
    <div>
      <Backdrop
        sx={{ color: "#fff", zIndex: (theme) => theme.zIndex.drawer + 5 }}
        open={open}
      >
        <CircularProgress color="inherit" />
      </Backdrop>
      {Item_Change()}
      {List_items()}
    </div>
  );
}
