import React, { useState, useEffect } from "react";
import ItemList from "./ItemsList/ItemList";
import ItemListSelf from "./ItemsList/ItemListSelf";
import Button from "@mui/material/Button";
import TextField from "@mui/material/TextField";
import Box from "@mui/material/Box";
import Grid from "@mui/material/Grid";
import Backdrop from "@mui/material/Backdrop";
import CircularProgress from "@mui/material/CircularProgress";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import API from "../utils/API";

const item_shem = {
  name: "",
  description: "",
  price: 0,
};

export default function Market() {
  let navigate = useNavigate();
  const [open, setOpen] = useState(false);
  const [myId, set_myId] = useState({});
  const [new_item, set_new_item] = useState(item_shem);

  useEffect(() => {
    axios
      .get(`${API.url()}/users/find/${API.getUserId()}`)
      .then((res) => {
        set_myId(res.data);
      })
      .catch((e) => console.log(e));
  }, []);

  const Information = () => {
    return (
      <Box sx={{ textAlign: "center" }}>
        <h1>Bienvenue {myId.pseudo}</h1>
        <h2>Argent disponible : {myId.money} â‚¬</h2>
      </Box>
    );
  };

  const Deconnection = () => {
    return (
      <Box>
        <Button
          color="secondary"
          onClick={() => {
            API.logout();
            navigate("/login");
          }}
        >
          DECONNEXION
        </Button>
      </Box>
    );
  };

  const AddItem = () => {
    return (
      <Box>
        <h2> Ajout d'un nouvel item</h2>
        <div>
          <TextField
            type="text"
            onChange={(e) =>
              set_new_item({
                ...new_item,
                name: e.target.value,
              })
            }
            value={new_item.name}
            label="nom"
            multiline
          ></TextField>
          <TextField
            type="text"
            onChange={(e) =>
              set_new_item({
                ...new_item,
                description: e.target.value,
              })
            }
            value={new_item.description}
            label="description"
            multiline
          ></TextField>
          <TextField
            sx={{ width: "15ch" }}
            id="standard-number"
            type="number"
            onChange={(e) =>
              set_new_item({
                ...new_item,
                price: e.target.value,
              })
            }
            value={new_item.price}
            InputLabelProps={{
              shrink: true,
            }}
            label="prix"
          ></TextField>
        </div>
        <Box>
          <Button
            onClick={() => {
              setOpen(true);
              console.log(new_item);
              API.createItem(new_item);
              setTimeout(() => {
                window.location.reload(false);
                setOpen(false);
              }, 3000);
            }}
          >
            ENVOI
          </Button>
        </Box>
      </Box>
    );
  };
  return (
    <div>
      <Backdrop
        sx={{ color: "#fff", zIndex: (theme) => theme.zIndex.drawer + 1 }}
        open={open}
      >
        <CircularProgress color="inherit" />
      </Backdrop>
      {Information()}
      <Grid container>
        <ItemList money={myId.money} /> <ItemListSelf />
      </Grid>
      {AddItem()} {Deconnection()}
    </div>
  );
}
